>\r\n					<li class="no-margin">\r\n						<a href="http://sergium.net" class="sergium"></a>\r\n					</li>\r\n					<li class="sponsors-valco-margin">\r\n						<a href="http://val.co.ua" class="valco"></a>\r\n					</li>\r\n					<li>\r\n						<a href="http://yothog.com" class="yothog"></a>\r\n					</li>\r\n				</ul>\r\n			</li>\r\n			<li class="content-sertificates">\r\n				<a href="#"></a>\r\n			</li>\r\n		</ul>', '123', '', 'inherit', 'open', 'open', '', '71-revision-2', '', '', '2013-02-25 01:54:30', '2013-02-24 21:54:30', '', 71, 'http://localhost/wp/?p=74', 0, 'revision', '', 0);
